	correctContent = JSON.parse(correctContent);
	const normalText = correctContent.replace(/\\u[\dA-Fa-f]{4}/g, match =>
		String.fromCharCode(parseInt(match.replace(/\\u/g, ''), 16))
	);
	let a = normalText.split("\\n");
	a = a.join(" ");
	a = a.split("\n");
	a = a.join(" ");
	return a
}

